definition.script:
#!/usr/bin/env groovy

@Library('jenkins-shared-lib')_

pipeline {
    agent any

    environment {
        PROJECT_NAME = get_project_name()
        PR_BRANCH = ""
    }

    stages{
        stage("git checkout"){
            steps{
                git credentialsId: 'Jenkins-shared-lib', url: 'git@github.com:guleriagishere/sharedlib.git'
            }

        }
        stage("One"){
            steps{
                echo "========executing A========"
            }
        }
    }
    post {
        always{
            slacknotification(currentBuild.currentResult,'itmavenworkspace','jenkins','#jenkins','slack-tokenId-credential',getTestResultsForSlack())
            // slackSend channel: '#jenkins', message: 'hello world', teamDomain: 'itmavenworkspace', tokenCredentialId: 'slack-tokenId-credential', username: 'jenkins'
        }
    }
}
